import React,{useEffect, useState} from 'react'
import axios from "axios"

export default function EditTodo(props) {
    const api = axios.create({
        baseURL:"http://localhost:4000"
    })
    const[todoData,setTodoData] = useState({})
    useEffect(()=>{
        api.get(`/edit/${props.match.params.id}`)
        .then(data=>{
            setTodoData(data.data)
        })
    })
    function updatedTodoData(e){    
        const name = e.target.name  
        const value = e.target.value
        setTodoData({
            [name]:value
        })
    }
    return (
        <div className="container">
            Edit Todo
            <form method="post">
                <div className="form-group">
                    <label htmlFor="todo">
                        Todo:
                    </label>
                   {/* @ts-ignore */}                        
                    <input type="text" name="todo" id="todo" value={todoData.todo} onChange={updatedTodoData} className="form-control" required/>
                </div>
                <div className="form-group">
                    <label htmlFor="priority">    
                        Priority:
                    </label>
                    {/*@ts-ignore*/}
                    <input type="text" name="priority" id="priority" value={todoData.priority} onChange={updatedTodoData} className="form-control" required/>
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary">Create Todo</button>
                </div>
            </form>
        </div>
    )
}
