import React,{useEffect, useState} from 'react'
import axios from 'axios'

const api = axios.create({
    baseURL:`http://localhost:4000`
})
export default function CreateTodo(props) {
    const[updateData,setUpdateData] = useState([{id:props.id,todo:"",priority:""}])
    const handleChange = (e)=>{
        const name = e.target.name;
        const value = e.target.value
        setUpdateData({
            ...updateData,
            [name]:value
        })
    }
    useEffect(()=>{
        async function loadIdData(id){
            await api.get("/todo/:id")
              .then(data=>{
                  setUpdateData(data.data)
              })
              .catch(err=>{
                  console.log(err)
              })
        }
        loadIdData()
    },[])
    return (
        <div className="container">
            <div className="mt-3">
                <h3>Edit Todo</h3>
            </div>
            <form method="post" onSubmit={(e)=>createTodoRequest(e)}>
                {updateData.map(update=>{
                   return  <>
                            <div className="form-group">
                                <label htmlFor="todo">
                                    Todo:
                               </label>
                                <input type="text" name="todo" id="todo" value={update.todo} onChange=  {handleChange} className="form-control" required/>
                            </div>
                            <div className="form-group">
                                 <label htmlFor="priority">
                                    Priority:
                                </label>
                                <input type="text" name="priority" id="priority" value={update.priority} onChange={handleChange} className="form-control" required/>
                            </div>
                            <div className="form-group">
                                 <button type="submit" className="btn btn-secondary">Edit Todo</button>
                         </div>
                    </>

                })}
            </form>
        </div>
    )

    async function createTodoRequest(e){
        e.preventDefault()
        await api.put('/update',updateData)
          .then(resp=>{
              alert(JSON.stringify(resp.data))
          })
          .catch(ex=>{
              alert(ex.message)
          })
    }    
}
