import React,{useState} from 'react'
import axios from 'axios'

const api = axios.create({
    baseURL:`http://localhost:4000`
})
export default function CreateTodo() {
    const [requestData,setrequestData] = useState({todo:"",priority:""})
    return (
        <div className="container">
            <div className="mt-3">
                <h3>Create Todo</h3>
            </div>
            <form method="post" onSubmit={(e)=>createTodoRequest(e)}>
                <div className="form-group">
                    <label htmlFor="todo">
                        Todo:
                    </label>
                    <input type="text" name="todo" id="todo" className="form-control" required/>
                </div>
                <div className="form-group">
                    <label htmlFor="priority">
                        Priority:
                    </label>
                    <input type="text" name="priority" id="priority" className="form-control" required/>
                </div>
                <div className="form-group">
                    <button type="submit" className="btn btn-primary">Create Todo</button>
                </div>
            </form>
        </div>
    )

    async function createTodoRequest(e){
        e.preventDefault()
        const request = {
            todo: document.getElementById('todo').value,
            priority:document.getElementById('priority').value
        }
        await api.post('/create',request)
         .then(resp=>{
             alert(resp.data.message);
         })
         .catch(err=>{
             console.log(err)
         })
    }

}
